--DECLARE @ID AS int
DECLARE @garment_des AS nvarchar(1024)
DECLARE @pat_no AS nvarchar(255)
DECLARE @l_desc AS nvarchar(1024)

DECLARE PatternDescriptionCursor CURSOR FAST_FORWARD FOR 
SELECT [garment_des]
      ,[pat_no]
      ,[l_desc]     
  FROM [BlackChrome].[dbo].[garspec1] 
OPEN PatternDescriptionCursor 
	FETCH NEXT FROM PatternDescriptionCursor INTO @garment_des, @pat_no, @l_desc
	WHILE @@FETCH_STATUS = 0 
	BEGIN 
	
	UPDATE [Indico].[dbo].[Pattern] SET [Description] = @l_desc  WHERE [Number] = @pat_no
	
		FETCH NEXT FROM PatternDescriptionCursor INTO @garment_des, @pat_no, @l_desc
	END 

CLOSE PatternDescriptionCursor 
DEALLOCATE PatternDescriptionCursor		
