    #region ArtWorkBOComparer
    public class ArtWorkBOComparer : IComparer<Indico.BusinessObjects.ArtWorkBO>
    {
        private string propertyToCompareName;
        public ArtWorkBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.ArtWorkBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.ArtWorkBO", "propertyToCompare");
            this.propertyToCompareName = propertyToCompare;
        }
        
        public ArtWorkBOComparer()
        {
        
        }

        #region IComparer<Indico.BusinessObjects.ArtWorkBO> Members
        public int Compare(Indico.BusinessObjects.ArtWorkBO x, Indico.BusinessObjects.ArtWorkBO y)
        {
            if (propertyToCompareName != null)
            {
                PropertyInfo p = typeof(Indico.BusinessObjects.ArtWorkBO).GetProperty(propertyToCompareName);
                return compare(p, x, y);
            }
            else
            {
                PropertyInfo[] arrP = typeof(Indico.BusinessObjects.ArtWorkBO).GetProperties();
                foreach (PropertyInfo p in arrP)
                {
                    int v = compare(p, x, y);
                    if (v != 0)
                        return v;
                }
                return 0;
            }
        }

        private int compare(PropertyInfo p, Indico.BusinessObjects.ArtWorkBO x, Indico.BusinessObjects.ArtWorkBO y)
        {
            object xVal = p.GetValue(x, null);
            object yVal = p.GetValue(y, null);

            if (xVal == null)
            {
                if (yVal == null)
                    return 0;
                else
                    return -1; // x is null, y is not, y is greater
            }
            else
            {
                if (y == null)
                    return 1; // x non null, y is null, x is greater
                else if (xVal is string)
                {
                    return StringComparer.OrdinalIgnoreCase.Compare(xVal, yVal);
                }
                else if (xVal is IComparable)
                {
                    return ((IComparable)xVal).CompareTo((IComparable)yVal);
                }
                else
                    throw new ArgumentException
                        ("is not string or valuetype that implements IComparable", "propertyToCompare");

            }
        }

        #endregion
    }
