        private int compare(PropertyInfo p, Indico.BusinessObjects.DeliveryOptionBO x, Indico.BusinessObjects.DeliveryOptionBO y)
        {
            object xVal = p.GetValue(x, null);
            object yVal = p.GetValue(y, null);

            if (xVal == null)
            {
                if (yVal == null)
                    return 0;
                else
                    return -1; // x is null, y is not, y is greater
            }
            else
