        }

        #region IComparer<Indico.BusinessObjects.InvoiceStatusBO> Members
        public int Compare(Indico.BusinessObjects.InvoiceStatusBO x, Indico.BusinessObjects.InvoiceStatusBO y)
        {
            if (propertyToCompareName != null)
            {
                PropertyInfo p = typeof(Indico.BusinessObjects.InvoiceStatusBO).GetProperty(propertyToCompareName);
                return compare(p, x, y);
            }
            else
            {
                PropertyInfo[] arrP = typeof(Indico.BusinessObjects.InvoiceStatusBO).GetProperties();
                foreach (PropertyInfo p in arrP)
                {
                    int v = compare(p, x, y);
