        public MYOBCardFileBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.MYOBCardFileBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.MYOBCardFileBO", "propertyToCompare");
            this.propertyToCompareName = propertyToCompare;
        }
        
