        }

        #region IComparer<Indico.BusinessObjects.MYOBCardFileBO> Members
        public int Compare(Indico.BusinessObjects.MYOBCardFileBO x, Indico.BusinessObjects.MYOBCardFileBO y)
        {
            if (propertyToCompareName != null)
            {
                PropertyInfo p = typeof(Indico.BusinessObjects.MYOBCardFileBO).GetProperty(propertyToCompareName);
                return compare(p, x, y);
            }
            else
            {
                PropertyInfo[] arrP = typeof(Indico.BusinessObjects.MYOBCardFileBO).GetProperties();
                foreach (PropertyInfo p in arrP)
                {
                    int v = compare(p, x, y);
                    if (v != 0)
