    #region OrderStatusBOComparer
    public class OrderStatusBOComparer : IComparer<Indico.BusinessObjects.OrderStatusBO>
    {
        private string propertyToCompareName;
        public OrderStatusBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.OrderStatusBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.OrderStatusBO", "propertyToCompare");
            this.propertyToCompareName = propertyToCompare;
        }
        
        public OrderStatusBOComparer()
        {
        
        }

        #region IComparer<Indico.BusinessObjects.OrderStatusBO> Members
        public int Compare(Indico.BusinessObjects.OrderStatusBO x, Indico.BusinessObjects.OrderStatusBO y)
        {
            if (propertyToCompareName != null)
            {
                PropertyInfo p = typeof(Indico.BusinessObjects.OrderStatusBO).GetProperty(propertyToCompareName);
                return compare(p, x, y);
            }
            else
            {
                PropertyInfo[] arrP = typeof(Indico.BusinessObjects.OrderStatusBO).GetProperties();
                foreach (PropertyInfo p in arrP)
                {
                    int v = compare(p, x, y);
                    if (v != 0)
                        return v;
                }
                return 0;
            }
        }

        private int compare(PropertyInfo p, Indico.BusinessObjects.OrderStatusBO x, Indico.BusinessObjects.OrderStatusBO y)
        {
            object xVal = p.GetValue(x, null);
            object yVal = p.GetValue(y, null);

            if (xVal == null)
            {
                if (yVal == null)
                    return 0;
                else
                    return -1; // x is null, y is not, y is greater
            }
            else
            {
                if (y == null)
                    return 1; // x non null, y is null, x is greater
                else if (xVal is string)
                {
                    return StringComparer.OrdinalIgnoreCase.Compare(xVal, yVal);
                }
                else if (xVal is IComparable)
                {
                    return ((IComparable)xVal).CompareTo((IComparable)yVal);
                }
                else
                    throw new ArgumentException
                        ("is not string or valuetype that implements IComparable", "propertyToCompare");

            }
        }

