        public PatternSupportFabricBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.PatternSupportFabricBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.PatternSupportFabricBO", "propertyToCompare");
            this.propertyToCompareName = propertyToCompare;
        }
        
        public PatternSupportFabricBOComparer()
