    #region PaymentMethodBOComparer
    public class PaymentMethodBOComparer : IComparer<Indico.BusinessObjects.PaymentMethodBO>
    {
        private string propertyToCompareName;
        public PaymentMethodBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.PaymentMethodBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.PaymentMethodBO", "propertyToCompare");
            this.propertyToCompareName = propertyToCompare;
        }
        
        public PaymentMethodBOComparer()
        {
        
        }

        #region IComparer<Indico.BusinessObjects.PaymentMethodBO> Members
        public int Compare(Indico.BusinessObjects.PaymentMethodBO x, Indico.BusinessObjects.PaymentMethodBO y)
        {
            if (propertyToCompareName != null)
            {
                PropertyInfo p = typeof(Indico.BusinessObjects.PaymentMethodBO).GetProperty(propertyToCompareName);
                return compare(p, x, y);
            }
            else
            {
                PropertyInfo[] arrP = typeof(Indico.BusinessObjects.PaymentMethodBO).GetProperties();
                foreach (PropertyInfo p in arrP)
                {
                    int v = compare(p, x, y);
                    if (v != 0)
                        return v;
                }
                return 0;
            }
        }

        private int compare(PropertyInfo p, Indico.BusinessObjects.PaymentMethodBO x, Indico.BusinessObjects.PaymentMethodBO y)
        {
            object xVal = p.GetValue(x, null);
            object yVal = p.GetValue(y, null);

            if (xVal == null)
            {
                if (yVal == null)
                    return 0;
                else
                    return -1; // x is null, y is not, y is greater
            }
            else
            {
                if (y == null)
                    return 1; // x non null, y is null, x is greater
                else if (xVal is string)
                {
                    return StringComparer.OrdinalIgnoreCase.Compare(xVal, yVal);
                }
                else if (xVal is IComparable)
                {
                    return ((IComparable)xVal).CompareTo((IComparable)yVal);
                }
                else
                    throw new ArgumentException
                        ("is not string or valuetype that implements IComparable", "propertyToCompare");

            }
        }

        #endregion
    }
