                // using System.Linq.Dynamic here in Dynamic.cs;
                if (sortDescending)
                    oQuery = oQuery.OrderBy(sortExpression + " desc").Skip(startIndex).Take((maximumRows == 0) ? Int32.MaxValue : maximumRows);
                else
                    oQuery = oQuery.OrderBy(sortExpression + " asc").Skip(startIndex).Take((maximumRows == 0) ? Int32.MaxValue : maximumRows);
            }
            else
                oQuery = oQuery.OrderBy(obj => obj.ID).Skip(startIndex).Take((maximumRows == 0) ? Int32.MaxValue : maximumRows);

            List<Indico.BusinessObjects.ProductionPlanningBO> productionplannings = IQueryableToList(oQuery);
            if (this.Context == null)
            {
                context.Dispose();
            }
            
            return productionplannings;
        }
        
        public int SearchLikeOrObjectsCount()
        {
            IndicoEntities context = (this.Context != null) ? this.Context.Context : new IndicoEntities();
            return (from o in context.ProductionPlanning
                 where
                    (this.ID == 0 || this.ID == o.ID) && 
                    (this.WeeklyProductionCapacity == 0 || this.WeeklyProductionCapacity == o.WeeklyProductionCapacity.ID) && 
                    (this.OrderDetail == 0 || this.OrderDetail == o.OrderDetail.ID) && 
                    (this.ProductionLine == null || this.ProductionLine == o.ProductionLine.ID) && 
                    (this.SewingDate == null || this.SewingDate == o.SewingDate) 

                 orderby o.ID
                 select o).Count();
            
        }
