    {
        private string propertyToCompareName;
        public SupplierBOComparer(string propertyToCompare)
        {
            PropertyInfo p = typeof(Indico.BusinessObjects.SupplierBO).GetProperty(propertyToCompare);
            if (p == null)
                throw new ArgumentException("is not a public property of Indico.BusinessObjects.SupplierBO", "propertyToCompare");
